mapper.select:
select * from bpolice left join board on bpolice.bno = board.bno
and ${searchType} like concat('%',#{keyword},'%')
order by bpolice.bno desc limit #{offset}, #{limit}

select * from bpolice

	and id = #{author}

		and ${searchType} like concat('%',#{keyword},'%')


order by bno desc
limit #{offset}, #{limit}

select count(*) from bpolice

	and id = #{author}

		and ${searchType} like concat('%',#{keyword},'%')
